css:
@-moz-document domain("worldofnerds.com") {
 *,
html,
margin {
     background-color: black !important;
     color: #565555 !important;
     border-color: grey !important;
}
 
input,
textarea,
.mentions {
     background-color: #565555 !important;
     color: white !important;
}
 
/*[class^="icon-"] {
     border: 1px solid !important;
     border-color: grey !important;
}*/
 
.btm-row {
     border: 1px solid white !important;
}
section#notifications .row, section#notifications-messages .row, section#notifications-contacts .row, #notifications-contacts-lists, section#notifications-votings .row, section#notifications-groups .row
{
    border: 0px !important;
	background: transparent !important;
    /*display: inline !important;*/
    width: 100% !important;
    position: absolute !important;
    z-index: 500 !important;
}
[class*="online-circle"] {
     background-color: #97bf0d !important;
}
 
.cw-header,
.cw-list {
     border: 2px solid #565555 !important;
}
 
.container .row,
.sidebar,
.js-feed,
.post,
.cw-body {
     border: 2px solid #565555 !important;
}
 
a, .post-content .external-link .link-inner, .link-title {
     color: green !important;
}
 
a[href^="#"] {
     color: orange !important;
}
 
a[href^="https://www.worldofnerds.com"],
a[href^="www.worldofnerds.com"],
a[href^="worldofnerds.com"],
a[href^="http://www.worldofnerds.com"],
a[href*="static.worldofnerds.de"]{
    color: red !important;
}
 
a[href*="worldofnerds.com/user/"] {
    border-bottom: 1px solid #565555 !important;
}
 
.active .fa-heart {
    color: #97bf0d !important;
}
[id*="notification"][id*="box"]
{
    border: 1px solid #565555 !important;
}

.feed-overlay .screen-bg {
    width: 100% !important;
    height: 100% !important;
    background: rgba(0,0,0,0.8)!important;
    position: fixed !important;
    top: 0 !important;
    left: 0 !important;
    z-index: 600 !important;
    overflow-y: auto !important;
}
}
