css:
@-moz-document domain("www.messenger.com") {
/*unread color*/

._1ht3._1ht1
{
    background-color: rgba(0, 132, 255, .2);
    -webkit-transition: background-color .5s
}
/*non icon stuff*/

._1ht5 ._1qt4
{
    display: none !important;
}
/*side bar size change*/

._1enh
{
    flex-basis: 74px;
    min-width: 74px;
    transition: flex-basis 0.1s, min-width 0.1s;
}

.safari.webkit ._1enh
{
    -webkit-flex-basis: 74px;
    -webkit-transition: -webkit-flex-basis 0.1s, min-width 0.1s;
}
/*hide "messenger" text*/

._36ic ._1tqi
{
    display: none
}
/*messenger header centering*/

._36ic
{
    justify-content: center
}

.safari.webkit ._36ic
{
    -webkit-justify-content: center
}
/*new message tab*/

._4wzs
{
    overflow-x: hidden;
}

._1bqr
{
    display: none
}
/*option gear*/

._4kzu
{
    display: none;
}
/* hides search bar */

._5iwm
{
    display: none
}
/* Hides gear at profile pictures as well as small profile pictures in corners */

._2j6._5l-3
{
    display: none;
    visibility: hidden;
}

._3d85
{
    display: none;
    visibility: hidden;
}

.uiPopover._6a._6b
{
    display: none;
    visibility: hidden;
}

._5blh._4-0h._p
{
    display: none;
    visibility: hidden;
}

._5bli._2_a2.img
{
    display: none;
    visibility: hidden;
}
}
