css:
@-moz-document url-prefix("https://hg.") {
/* main divs */
#header div#header-inner, #footer div#footer-inner, #content div.box div.title, #login div.title {
    background-image: linear-gradient(to bottom, #6688AA, #7799BB);
}

/* menus */
#header #header-inner #quick ul, #revision-changer, #context-pages, #context-pages ul, .repo-switcher-dropdown.select2-drop.select2-drop-active, #quick_login, #content #context-bar {
    background-image: linear-gradient(to bottom, #445566, #557799);
}

body{
    background-image: linear-gradient(to bottom, #334466, #112244);
}

a, #compare_branches + .table .revision-link, #compare_tags + .table .revision-link, #compare_bookmarks + .table .revision-link, .table #files_data .revision-link, #repos_list_wrap .revision-link, #shortlog_data .revision-link {
    color: #335577;
}

.repotag, .branchtag, .tagtag, .booktag, .spantag {
    color: #6688AA;
    border-color: #6688AA;
}

a:hover {
    color: #6688AA;
}

.header>img {
    /*filter: grayscale(100%);*/
    filter: hue-rotate(120deg);
    -webkit-filter: hue-rotate(120deg);
}
}
