css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("spiegel.de") {
    
    body {
        background-color: #41464D !important;
    }

    body > div > div > img,
    #wrapper > div > img,
    .article-section > div > div,
    #spColumnAd,
    .adition
    {
        display: none;
    }
    
    #wrapper, #spOasTop {
      margin: 0 !important;
    }
    
    #wrapper > #wrapper-shadow {
        margin: 0 auto;
        padding: 0;
    }

    p {
        text-align: justify; 
    }
    
    .article-comments-box
    {
        display: none;
    }
  
}
