css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nyaa.se") {
.viewdescription, .spoiler { border: 0px solid gray; box-shadow: 0px 0px 2px gray;}
  .content { box-shadow: 0px 0px 5px; border: 0px none; border-radius: 0px 0px 15px 15px; background-image: linear-gradient(transparent 5%, rgba(200, 200, 200, 0.3));}
  .viewdownloadbutton > a > img, .viewdownloadtxtbutton > a > img { display: none}
  .viewdownloadbutton > a:hover, .viewdownloadtxtbutton > a:hover { text-decoration: none;}
  div.viewdownloadbutton > a::before, div.viewdownloadtxtbutton > a::before { display: inline-block; box-shadow: 0px 0px 2px 0px black; font-weight: bold; font-size: 120%; color: white; padding: 5px; border-radius: 3px; text-shadow: 1px 1px black; background-color: green; border: 1px solid darkgreen; border-top-color: lightgreen; border-bottom-color: black; background-image: linear-gradient(green, darkgreen)}
  div.viewdownloadbutton > a::before { content: 'Download';}
  div.viewdownloadtxtbutton > a::before { content: 'Text'; }
  div.trusted { background-color: white; border-top : 15px solid green;}
  div.remake  { background-color: white; border-top : 15px solid darkorange;}
  div.remake a { color: #955F03;}
  div.remake div.viewdownloadbutton > a::before, div.remake div.viewdownloadtxtbutton > a::before {  background-color: darkorange; border: 1px solid #955F03; }
  div.aplus   { background-color: aliceblue; border-top : 15px solid deepskyblue;}
  div.aplus a { color: midnightblue;}
  div.aplus div.viewdownloadbutton > a::before, div.aplus div.viewdownloadtxtbutton > a::before { background-color: deepskyblue; background-image: linear-gradient(deepskyblue, royalblue); border-color: royalblue; border-top-color: #A9F2FF; border-bottom-color: darkblue;}
/* Uncomment the following line to hide the comments and/or the chatbox */
   /* .comment, .chatbox { display : none !important; } */

}
