css:
@-moz-document url-prefix("http://usmbc.co.kr/vod_player/") {
body { margin:0; }
body>table td[height="13"] { display:none; }
body>table td[background] { display:none; }
tr[height="47"] { display:none; }
tr[height="47"]+tr>td { padding:0 !important }
.adsbygoogle { display:none !important; }
#mediaplayer_wrapper { width:100% !important; height:100% !important; }
tr[height="100%"]+tr { display:none; }
.jwplayer { width:auto !important; height:100% !important; }
}
