css:
@-moz-document domain("streamable.com") {
body, html, .card, .video-item.grid .card .card-footer, .dropdown .actions-menu, .settings .card .card-footer, .modal-content, .landing , .landing .pane-title, .signin .separator span, #page_content{
    background-color: #161616 !important;
}
.app-nav .navbar, .app-container .video-creator .url-input, .form-control, .button, button, .landing .navbar-default, .nav-tabs .nav-link.active, .page-content, .box, #player.container .actions-section, .video-stats .card-header{
    background-color: #202020 !important;
}
img.logo, .landing .features-pane .feature span svg, .landing .more-arrow svg, .header .logo {
    filter: invert(100%) grayscale(100%);
}
body, html, .card, .video-item.grid .card .card-footer, .app-nav .navbar .nav-item, .app-container .video-creator .url-input, .videos-list .video-list-metadata .sort-control select, .videos-list .video-list-metadata .sort-control svg, .videos-list .video-list-metadata .search-control .filter-input, .video-item .edit-title-input, .button, button, .form-control, .settings .header .user-metadata .username, .landing .start-pane h1, .landing .features-pane .title-feature, .landing .pane-title, .landing .examples-pane .example .title, .landing .features-pane .feature .feature-description h3, .landing .more-arrow .more-arrow-description, .landing .navbar-nav .nav-btn, .nav-tabs .nav-link.active, .page-title, h1, h2, h3, h4, h5, #player.container #title, #player.container .visits-button, .video-stats .card-header {
    color: #CCCCCC !important;
}
.videos-list .page-item .page-link:hover, .app-container .main-footer li a:hover, .landing .footer li a:hover, #player.container .visits-button:hover {
    color: #828282 !important;    
}
.videos-list .page-item.active .page-link {
    color: #115bff !important;
}
.app-container .main-footer, .modal-footer, .landing .examples-pane {
    border-top: 1px solid #000 !important;
}
.app-nav .navbar, .app-container .video-creator .url-input, .modal-header, .embed-maker .embed-section-header, .landing .navbar-default , .landing .examples-pane, #player.container #video-footer, .video-stats .card-header{
    border-bottom: 1px solid #000 !important;
}
.video-item .edit-title-input, .button, button, .dropdown .actions-menu, .form-control, .landing .features-pane .title-feature, .landing .footer-cta-pane {
   border: 1px solid #000 !important;
}
.app-container .video-creator .url-input:hover, .video-item .edit-title-input:hover {
    border-color: #000 !important;
}
.button, button {
    background: linear-gradient(180deg,#202020,hsla(0,0%,100%,0)) 0 no-repeat,50% no-repeat #202020 !important;
}
.button, button:hover {
    background: linear-gradient(180deg,#161616,hsla(0,0%,100%,0)) 0 no-repeat,50% no-repeat #161616 !important;
}
.signin .separator, .page-content:before {
    background-color: #000 !important;
}
}
