css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.inoreader.com") {

/*sidepanel*/

 #tree_pane {
    background-color: #e6e6e6;
    line-height: 2.5;
    font-size: 1.1em;
 }

 .parent_div_inner_selected {
    background-color: #fff;
 }

 #subscription_options_tick {
    background-color: #e6e6e6;
 }

/*topbar*/

 #sb_tp_dock{
    background-color: #e6e6e6 !important;
 }

 #sb_tp_search_overlay, #feed_searcher {
    background-color: #e6e6e6;
    background-image: none;
 }

 #subscriptions_buttons, #sb_rp_tools {
    background-color: #fff;
    background-image: none;
 }

 .inno_toolbar_button {
    background-color: #fff !important;
 }

/*titles_read_articles*/
 .ar.article .article_header_title {
    opacity: 0.4;
 }

}
