css:
@-moz-document url-prefix("https://postimage.io")  /* spelled-out "image" */

      {

/* "LOGGED-OUT HOMEPAGE & UPLOADING PAGES ONLY" CHANGES (URL = postimage.org) - NOTE "image" is spelled out
   on LOGGED-OUT HOMEPAGE & UPLOADING PAGES ONLY (postimage.org), otherwise it is "...img" (postimg.org) */

  /* On LOGGED-OUT HOMEPAGE & UPLOADING PAGES ONLY, push top down 40 pixels, to better center contents */

html {transform:translateY(40px)!important;}

  /* Recolor elements */
  
* {background:black!important; color:dimgray!important; border-color:dimgray!important; text-shadow:none!important;}

#header a {color:cyan!important;}

a, a * {color:cyan!important; text-decoration:none!important;}

#header a:hover, a:hover, b:hover, label:hover {color:red!important;}

div#status_uploading.progress.overall-progress {background-color:cyan!important}

  /* Remove "Add to Favorites" (bookmark) link, and all Footer links */

span#bmc, #footer {display:none!important;}

}


@-moz-document domain("postimg.org")  /* abreviated "img" */

      {

/* LOGGED-IN & OTHER-THAN-HOMEPAGE CHANGES (URL Prefix = "http://postimg.org/...") */

  /* Recolor elements */
  
* {background:black!important; color:dimgray!important; border-color:dimgray!important; text-shadow:none!important;}

#header a {color:cyan!important;}

a, a * {color:cyan!important; text-decoration:none!important;}

#header a:hover, a:hover, b:hover, label:hover {color:red!important;}

div#status_uploading.progress.overall-progress {background-color:cyan!important}

  /* Remove "Add to Favorites" (bookmark) link, and all Footer links */

span#bmc, #footer {display:none!important;}

}
