@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("shintranslations.com") {

/*!========================================\\
|| Dark Shin Translations                  ||
||-----------------------------------------||
|| Created by ARH                          ||
||-----------------------------------------||
|| A simple dark theme for ShinTranslations||
|| Especially usefull when reading at night||
||-----------------------------------------||
|| contact: hakerti@wp.pl                  ||
\\----------------------------------------!*/

  ::-moz-selection {
    background: #444444;
    color: #bbbbff;
  }
  
  div#content {
    background-color: #222222;
  }
  
  div#page > header {
    background-color: #444444;
  }
  
  .hentry {
    width: 1400px !important;
  }
  
  p {
    margin: auto !important;
  }
  
  div.hentry-wrapper > article {
    padding: 20px;
  }
  
  #comments {
    display: none;
  }
  
  footer#colophon {
    display: none;
  }
}
