css:
@-moz-document domain("blogspot.kr") {
  .columns, .column-right-outer, #sidebar-right-1, aside, .column-right-inner, .column-right-outer {
    width: 700px !important;
  }

  .fauxcolumn-outer, .fauxcolumn-right-outer {
    display: none !important;
  }

  .columns, .column-right-outer {
    float: none !important;
    margin: 0 auto !important;
    padding: 0 !important;
    border: none !important;
    display: block !important;
  }

  .columns-inner, .aside {
    font-family: "KoPubBatang", "나눔명조", "맑은,  고딕", "나눔바른고딕", "나눔고딕" !important;
    line-height: 1.85em !important;
  }

  p, li {
    font-size: 1.5em !important;
    line-height: 1.85em !important;
    text-align: justify !important;
  }

  blockquote  {
    padding-right: 60px !important;
  }

  blockquote, em  {
    font-style: normal !important;
  }
}
