css:
@-moz-document domain(web.groupme.com) {
.chat-messages, .message, .chat-header, .chat-sidebar, .chat-sidebar *, .chat-input, .mousetrap, .dropdown-menu, .dropdown-menu a,
.form-horizontal, .form-actions, .device-notifications-toggle, .paste {
    background-color: #222 !important;
    color: #bbb !important;
}
.inline-downloader, input {
    background-color: #333 !important;
    color: #bbb !important;
}
.timestamp, .system .body {
    background-color: #222 !important;
    color: #f2f2f2 !important;
}
body {
    background-color: #000 !important;
    color: #F2F2F2 !important;
}
.navbar-inner, .dropdown-menu a:hover, button {
    background-color: #025 !important;
    color: #bbb !important;
}
.burger-box:hover, button:hover {
    background-color: #047 !important;
    color: #F2F2F2 !important;
}
.paste {
    border-left: rgba(255, 255, 255, 0.2) solid !important;
}
.chat-messages .timestamp-divider {
    background-color: #222 !important;
}
.chat-messages .timestamp-divider {
    background-color: #000 !important;
    color: #FFF
}
.message-composer {
    background-color: #000 !important;
    color: #FFF
}
.pillar {
    background-color: #000 !important;
    color: #FFF
}
h2.chat-name {
    color: #FFF
}
#tray .tray-header {
    background-color: #000 !important;
}
#tray .tray-header .row{
    background-color: #000 !important;
}
div.list-item{
    background-color: #000 !important;
    color: #FFF
}
.groupme-list .list-heading{
    color: #EEE
}
#tray .tray-list{
    background-color: #000;
}
}
