css:
@-moz-document domain("waifu2x.udp.jp") {
body {
    padding-top: 10px;
    padding-bottom: 20px;
    background-color: #181818;
    background-repeat: repeat;
    background-attachment: fixed;
    background-position: left top;
    background-size: cover;
    color: #fff;
    margin-left: auto;
    margin-right: auto
}

a, a:visited, a:active {
    color:#905d5d;
    
}
    
form {
    display: block;
    margin-top: 0em;
    background: #323232;
}


.button {
    min-width: 128px;
    min-height: 2em;
    margin: 4px;
    color: #ebe0e0;
    background-color: #727272;
    border-color: #323232;
}

.button:hover {
    color: #ebe0e0;
    background-color: #905d5d;
    border-color: #4e3737;
}

.github-banner {
    display: none;
}
}
