css:
@-moz-document domain("totobro.com"), regexp("totobro.com") {
div {
    background: #292929 !important;
    color: #bbb !important;
}
a {
    color: #ea0 !important;
    border-bottom: 1px solid transparent !important;
    transition: all ease-out 0.5s;
    text-decoration: none !important;
}
a:hover {
    border-bottom: 1px solid #ea0 !important;
}
.sub-menu a {
    background: #202020 !important;
}
input {
    background: #191919 !important;
    color: #bbb !important;
}
#facebook-likebox-2 *,
#text-8 * {
    opacity: 0.6 !important;
    transition: all ease-out 0.5s;
}
#facebook-likebox-2 *:hover,
#text-8 *:hover {
    opacity: 1 !important;
}
}
