css:
@-moz-document domain("www.stackage.org") {
body {
    background-color: #222;
    color: #eee;
	font-family: Lato, sans-serif;
	font-size: 16px;
	text-shadow: none;
}

div.well {
    background-color: #333;
}

h1, h2, h3, h4, h5 {
    color: #1155bb;
}
textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
    background-color: #333;
    color: #eee;
}

.markdown-container {
    background-color: #223333;
    border: none;
    color: #eee;
}

.markdown-container h1, .markdown-container h2, .markdown-container h3, .markdown-container h4, .markdown-container h5 {
    color: #ddddff;
}

.markdown-container pre, .markdown-container pre code, .markdown-container code {
    background-color: #111;
    color: #ddd;
}

.hljs-class .hljs-title, .hljs-type, .vhdl .hljs-literal, .tex .hljs-command {
    color: #ddd;
    font-weight: normal;
}

.hljs-title, .hljs-string, .hljs-comment, .diff .hljs-header, .hljs-keyword, .hljs-winutils, .nginx .hljs-title, .hljs-subst, .hljs-request, .hljs-status {
    color: #ddd;
    font-weight: normal;
}

.expanding .bottom-gradient {
    background: none;
}

.expanding .fa {
    color: #ddd;
}

}
