@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url-prefix("about:preferences") {
  
label.category-name {display:none !important;}
richlistitem {padding-right: 15px !important;}
prefpane {max-width: none !important;}
  
[class="category"][selected="true"] {background-color: #ff9500 !important;}
  
[class="header"] {
  margin-top:-40px !important;
  margin-bottom:0px !important;
}
  
/*search list*/
treecol#engineKeyword {max-width: 150px !important;}
tree#engineList {height:90vh !important;}

/*applications*/
#applicationsContent {height:90vh !important;}
  
/*Offline Web Content and User Data*/
listcell:last-child {min-width: 100px !important;}

groupbox#dialogBox {width:90vw !important; height:90vh !important;}

}
