css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("baidu.com") {

  table[width="25%"][align="right"],

  table[width="760"][height="90"],

  table[id^="rightAd"],

  div[id^="Tads"], 

  div[id^=marqueebox],

  div[id~=pma],

  a[href^="http://www.baidu.com/baidu.php?"],

  a[href^="http://www.baidu.com/baidu.php?"] ~ *{

    display:none!important;

  }
  	a {
		color: green !important;
	}

	a:visited {
		color: grey !important;
	}

	a:active {
		color: #cc0066 !important;
	}
.f{width:1000px !important;
margin-top:10px !important;
background-color: #ebeff9 ! important;
border-top: solid 1px #cc0066 ! important;
-moz-border-radius: 0px ! important;
padding: 10px 30px! important;
margin: 8px 0px !important;}
}
@-moz-document url-prefix("http://hi.baidu.com"){#m_comm_info a[href="http://www.baidu.com/search/whatisrss.html"]{display:none !important;}}
@-moz-document url-prefix("http://tieba.baidu.com"), url-prefix("http://post.baidu.com") {
html > body > div:first-child + form + div,
html > body > div:first-child + div + form + div,
#rightad,.pad0l,.mod { display: none !important; }
}
@-moz-document url-prefix(http://zhidao.baidu.com/)
{
#main > div:first-child,#right,#cproshow,#centertop_be,#centerbutton_be,#right img,.r{display:none !important;width:0% !important;}
#center>table{
margin-top:10px !important;
background-color: #ebeff9 ! important;
border-top: solid 1px #cc0066! important;
-moz-border-radius: 0px ! important;
padding: 8px ! important;
margin: 8px 0px !important;}
}
@-moz-document url-prefix("http://news.baidu.com")
{div>table{
margin-top:10px !important;
background-color: #ebeff9 ! important;
border-top: solid 1px #cc0066! important;
-moz-border-radius: 0px ! important;
padding: 10px 30px! important;
margin: 8px 0px !important;}
a {text-decoration: none ! important; }
.p2,#divpost,#ecad,#ecomaddiv{display:none !important;}
}
@-moz-document url-prefix("http://mp3.baidu.com")
{#leftres>table>tbody{
margin-top:10px !important;
background-color: #ebeff9 ! important;
border-top: solid 1px #cc0066! important;
-moz-border-radius: 0px ! important;
padding: 10px 30px! important;
margin: 8px 0px !important;}
a {text-decoration: none ! important; }
.m,.pe,.qx,#sma,#tbmp3,#mztop,body>table>tbody>tr>td>table>tbody>tr>td>div{display:none !important;}
}
