css:
@-moz-document domain('bitbucket.org'){

/*Issue title in issue pages*/
#issue-title {
    direction: rtl;
}

#id_content{
      direction: rtl;
  text-align: right;
}

input[type="text"] {
    direction: rtl;
  text-align: right;
}

/* content of issue*/
.issue-description.wiki-content {
   direction: rtl;
  text-align: right;
}

/* issue title in issues page*/
.text.flex-content--column {
   direction: rtl;
  text-align: right;
}

textarea {
    direction: rtl;
  text-align: right;
}
}
