css:
@-moz-document domain(github.com) {
/* 
 *
 * repository-content block
 *
*/

.blob-line-code, .diff-line-code, .blob-line-num, .blob-code, .blob-code-inner, .blob-num {
  font-family: "Source Code Pro", "Source Han Code JP", Consolas, "Liberation Mono", Menlo, Courier, monospace;
  /* "Source Code Pro", */
  /* "Source Han Code JP", */
  
  font-weight: 500;
  -webkit-font-smoothing: antialiased;
  font-size: 15px;
  line-height: 1.8;
}

table.js-file-line-container {
  background-color: rgba(0, 0, 0, 0.04)
}
/* 
 *
 * Codemirror block
 *
*/

.tab-size[data-tab-size='2'], .tab-size[data-tab-size='4'], .tab-size[data-tab-size='8'], .inline-review-comment, .gist table.lines {
  tab-size: 4;
}

.pl-c {
  color: rgb(140, 142, 140);
}

.pl-c1 {
  font-weight: 500;
}

.pl-smi {
  font-weight: 400;
}

.pl-k ~ .pl-en {
  font-weight: 500;
}

.blob-code-inner span.pl-en > span.pl-v {
  font-weight: 400;
}

.blob-code-inner > span.pl-en > span.pl-en {
  font-weight: 500;
}
  }
