css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("shoutcast.com") {

#cookies, header.homeheader .slide-infos, header.homeheader .slide-infos .grid-pad, .col-1-1, header.homeheader #header-up, #hero {height: 0px !important;}
#cookies, #cookies .grid-pad, header.homeheader .slide-infos .grid-pad, .col-1-1, header.homeheader #header-up, #hero {display: none;}

header, header.homeheader {
    margin-top: 0px;
}

main.homePage {
margin-top: 0px !important;
}

#search-form button {
filter: invert(95%);
}    
    
/* Backgrounds */    
    
main {
    background: none repeat scroll 0% 0% black;
}

#playlist tr:nth-child(2n), #search-form input {
    background-color: #0c0c0c;
}   

/* Fonts */        
    
html, td.genre a, td.radio-name a, #search-form input {
    color: #ccc;
}    
    
main aside a {
    color: rgb(88, 99, 109);
    border-bottom: 2px solid black;
}

main a:hover {
    color: rgb(88, 149, 169);
}

/* Borders */    
    
.data-table tbody tr, header {
    border-bottom: 1px solid rgb(40, 40, 40);
}



}
