css:
@-moz-document domain(facebook.com) {
html {
	min-height:100%;
}

body.fbIndex {
	background: url("http://image.noelshack.com/fichiers/2015/52/1450736795-be8a1d39119c7ad9cca902d0be0c88c7-large.png") 50% 50% no-repeat !important;
	background-size: cover !important;
	height:100%;
	overflow:hidden;
}

.loggedout_menubar_container {
	background:none !important;
}
.loggedout_menubar {
	background:rgba(0,10,100,.2) !important;
	
	padding:0 10px 14px 10px;

	-webkit-border-top-right-radius: 4px;
        -webkit-border-bottom-left-radius: 4px;
	border-top-right-radius: 10px;
        border-bottom-left-radius: 10px;

	-webkit-box-shadow:  0px 2px 4px  rgba(0, 0, 0, .4);
	box-shadow:  0px 2px 4px  rgba(0, 0, 0, .4);
}

.loggedout_menubar label,
.login_form_label_field a {
	color:#fff !important;
}
.fbIndex #globalContainer #dropmenu_container,
.fbIndex #globalContainer #content,
.fbIndex #globalContainer #pageFooter {
	display: none !important
}

.fbIndex .loggedout_menubar_container {
	position: fixed !important;
	width: 480px !important;
	height: 82px !important;
	min-width: 0 !important;
	top: 13% !important;
	left: 20% !important;
	margin: -60px 0 0 -240px !important;
	z-index: -1 !important;
}
}
