css:
@-moz-document url-prefix("https://www.youtube.com/playlist?"), url-prefix("https://www.youtube.com/view_play_list?") {

.pl-message-panel { margin-bottom: .5em }

.pl-video { height: auto }

.pl-video > td, 
.pl-video.has-annotation > td,
.pl-video .more-menu-wrapper {
	padding-top:    2px;
	padding-bottom: 2px;
}
.pl-video .pl-video-thumbnail { padding: 1px 0 }

.pl-video.has-annotation td { vertical-align: middle }
.pl-video.has-annotation .pl-video-index {
    line-height: 1;
    padding-top: 2px;
}
.pl-video-annotation-container {
    margin-top: 0;
}

}

@-moz-document url-prefix("https://www.youtube.com/watch") {

#player-playlist .playlist-videos-list li {
	padding-top:    .2em;
	padding-bottom: .2em;
}
#player-playlist :not(.watch-queue-nav) .playlist-videos-list li.currently-playing {
	height: auto;
}

.playlist-videos-list .yt-ui-ellipsis { line-height: 1em }

}
