css:
@-moz-document domain('kanbanflow.com') {

/*Background*/    
    #board-wrapper{background-color: #182434 !important;}


/*Swimlane Header / Row headers*/
.board-swimlane-header{ background-color:#304a69 !important;}
.board-swimlane-name{color:#CFB596;}    
    
    
/*Column headers*/
.columnHeader-inner{background-color: #304a69 !important;}
.columnHeader-name, .columnHeader-count{color:#CFB596;}

/*Task Headers*/
.board-taskListCellHeader-inner{background-color: #40638c;}
.board-taskListCellHeader-taskCount{color:#CFB596 !important}
  
/*Task Container*/
.board-taskListCell-inner{background-color: #a7bcd7 !important;}       
    
/*Task Group (Date)*/
.task-groupLabel{color:#584328}

}
