css:
@-moz-document domain("animeshow.tv") {
body {
    background: rgb(13, 13, 13);
    color: white;
}
.img_bg3 > img {
    height: 622 !important;
    width: 700 !important;
    /* these numbers match the new image's dimensions */
    
    padding-left: 99999125px !important;
    padding-top: 25px !important;
    background: url(http://i.imgur.com/aziIhfN.png) no-repeat !important;
}
.img_bg2 > img {
    height: 622 !important;
    width: 700 !important;
    /* these numbers match the new image's dimensions */
    
    padding-left: 99999125px !important;
    padding-top: 25px !important;
    background: url(http://i.imgur.com/NYoITJd.png) no-repeat !important;
}
.navbar-static, footer, div#search_cointainer, .container, .episode_mirrors_wraper_focus, .episode_mirrors_wraper:hover, #sidebar_content > ul > li > a:hover {
    background: rgb(66, 66, 66) !important;
    background: rgba(66, 66, 66, .97) !important;
}
.genres_result_hover:hover {
    background: rgb(66, 66, 66) !important;
    background: rgba(66, 66, 66, .3) !important;
}
h3, h2, h1, .btn-default {
     background: rgb(66, 66, 66) !important;
}
#new_anime, #latest_anime, #sidebar_content > ul > li > a, #popular_anime, #anime, #episodes_list, #anime_list, #episode, #episode_disqus, #genres, #genres_result {
     background: rgb(114, 114, 114) !important;
}
.hidden-xs+ .row, .popular_anime_title, #search_cointainer > ul > li:hover {
    background: rgb(114, 114, 114) !important;
     background: rgba(114, 114, 114, .7) !important;
}
.col-xs-10, .latest_episode_title, .episodes_list_hover, #anime_list :nth-child(1), .episode_mirrors_type_sub , .episode_mirrors_name, #episode_additional_informations a, .author {
    color: white !important;
}
.col-xs-2, .col-md-6 {
    color: black !important;
}
.nav > li:hover, #nav ul li:hover {
    background: rgb(44, 44, 44) !important;
}
.popular_anime_hover, .img-responsive {
    border: none !important;
}
#disqus_thread {
    background: rgb(150, 150, 150) !important;
}
}
