css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("bilibili.com") 
{	/*小标签屏蔽*/
	.header-layer , .header-link ,.footer, .yoo, .live, .i-link[title="周边"],.i-link[title="游戏中心"],.i-link[title="BML"],.i-link[href="/square"], .menu-r, .new-icon,.m-i-square
	{ display: none !important; }
	#dynamic_num_total.num, .num, .heartBeatCount
	{ background-color: rgba(255, 255, 255, 0) !important; }
	/*搜索框调整*/
	::-moz-placeholder { font-size: 0px; }
	.header .num .search
	{ position: absolute;
	top: 15px;
	right: -2px;
	width: 240px;
	background-color: rgba(0, 0, 0, 0.02);
	}
    /*预览屏蔽*/
	.m-i[data-tid="155"], .m-i-live,.pmt-list, .preview>.medal, .preview>.fake_danmu_gen_shared,.b-r.bangumi,.nav-list,.n-i.gotop,.n-i-mlink-bg
	{ display: none !important; }
	/*模块屏蔽*/
	#b_dance, #b_ent, #b_fashion, #b_game, #b_kichiku, #b_live,#b_promote, #b_recommend, #b_technology,#b_teleplay,#b_life
	{ display: none !important; }
}


@-moz-document url-prefix("http://www.bilibili.com/video") ,
	url-prefix("http://bangumi.bilibili.com/") 
{	/*播放页面清理*/
	.mobile-link-l , .tag-list li.tag-item .hot ,	#bpoints_win, .arc-toolbar, .recommend-area, .sign-bg, .v-live-recommend
	{ display:none !important;}	
    .v1-bangumi-info-operate,.v_small
	{ display: none !important; }
}
