css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("krautchan.net") {
/*Design*/
div#WzTtDiV {
 display: none !important; }

body{
background-color: #1D1F21 !important;
max-width: 1900px;
word-wrap: break-word;
}


span.nav_group_button 
{
 color: #fff !important; 
}

table{
   word-wrap: break-word;
}

.postreply{
    max-width: 1800px;
word-wrap: break-word;
background-color: #282A2E !important;
}

.postreply_highlighted {
border: 1px groove #000  !important;
background-color: #1D1D21 !important; }

.postsubject{
    font-size:140% !important;
    color: #DE9869 !important;
}

.postername{
color: #C5C8C6 !important;
}

.file_thread{
background-color: #1D1F21 !important; 
border: 0px !important;
}

.file_reply{
background-color: #282A2E !important; 
border: 0px solid #1D1F21 !important;
}

.filename {
    text-decoration: underline !important;
}
.filename > a{
color: #6FB1DE !important;
}

span.hidden_thread{
background-color: #282A2E !important; 
border: 2px solid #1D1F21 !important;
}

hr {
border: 1px solid !important; border-color: #282A2E !important;
}

a:hover, .boardlist a {
 text-decoration: underline !important;  color: #6FB1DE !important;  
}

a {
 text-decoration: none !important; color: #6FB1DE  !important; }

.boardlist a {
 font-size: 90% !important; color: #C5C8C6 !important; 
}

.boardlist a:hover {
 background: #C5C8C6 !important; color: #C5C8C6 !important; }

h1, form, td, .postbody, .newsbody, .site_name 
{
 color: #C5C8C6 !important; 
}

.quote{
color: #96BD68 !important;
}

.helptip {
font: #1D1F21 !important;
}

.quotelink{
color: #6FB1DE !important;
}
blockquote > p > a{
color: #6FB1DE !important;
}

.postheader > a{
color: #C5C8C6 !important;
}

.label{
background-color: #1D1F21 !important;
}

.postform{
background-color: #1D1F21 !important;
}

#postform{
background-color: #1D1F21 !important;
}

h2{
color: #000 !important;
background-color: #282A2E !important;
}

div.report_field.popup {
 background: #C5C8C6 !important;
}

table{
    border-color: #282A2E !important;
}

}
