css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("zoomit.ir") {
  .row, div.itemIntroText, div.catItemHeader h3.catItemTitle a, div.k2ItemsBlock ul li a.moduleItemTitle{
      background: #2A2B2F;
      color: #eee;
  }
  a.moduleItemTitle {
    color: #ddd;
  }
  body {
    background: #222;
  }
  h1, h2, h3, h4, h5, h6,.table th{
    color: #fff;
  }
  .table-striped tbody > tr:nth-child(2n+1) > td, .table-striped tbody > tr:nth-child(2n+1) > th {
    background-color: #313338;
  }
  #comments .odd, #comments .even {
    background-color: #eee;
  }
}
