css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("smbc-comics.com")
{

  /* normalize top */
  body > center:first-child > table:first-child > tbody > tr:first-child > td:first-child > iframe,
  body > center:first-child > table:first-child > tbody > tr:first-child > td:first-child > [class^="__noscriptPlaceholder__"]
  { display: none !important; }

  #aftercomic
  {
    display: block !important;
    float: none !important;

    z-index: 9999 !important;
    position: absolute !important;
    top: 600px !important;
    left: 65% !important;

    background: white !important;
    padding: 5px !important;
    border: thick solid black !important;
  }

} /* 20081119 */
