css:
@-moz-document url-prefix("http://archives.nd.edu"), url-prefix("http://latin.campus.nd.edu") {
a:link {
    color: #a90329 !important;
}
a:visited {
    color: #740290 !important;
}
a:active {
    color: #ffcc00 !important;
}
[src|="mark150.gif"] {
    display: none !important;
}
input[value="Latin to English:"],
input[value="English to Latin:"] {
    background: -webkit-linear-gradient(top, rgba(136,3,169,1) 0%,rgba(116,2,144,1) 44%,rgba(88,0,110,1) 100%);
    border-color: #740290;
    color: #ffcc00;
}
input[name="keyword"],
input[name="english"] {
    background: #111;
    border-color: gray;
    color: white;
}
input:-webkit-autofill {
    -webkit-box-shadow: 0 0 0px 1000px #a90329 inset;
    -webkit-text-fill-color: #ffcc00 !important;
}
body {
    background: #111 !important;
    color: #ECECEC !important;
}
}
