css:
@-moz-document domain("sisainlive.com") {
  #Warp > table > tbody > tr > td:nth-of-type(3),
  #articleBody > div:nth-of-type(1) > table:nth-of-type(1),
  #scrollDiv,
  #iwm_float_wrapper,
  .adsbygoogle {
    display: none !important;
  }

  table {
    float: none !important;
    margin: 0 auto !important;
  }

  table, tbody, tr, td {
    border: none !important;
    background: none !important;
  }

  #articleBody > div,
  #articleBody > div > p {
    font-family: "KoPubBatang", "나눔명조", "맑은 고딕", "나눔바른고딕", "나눔고딕" !important;
    line-height: 1.85em !important;
  }

  #articleBody > div > p {
    font-size: 1.3em !important;
    word-break: keep-all;
    text-align: justify;
  }
}
