css:
@-moz-document domain("newsis.com") {
  table:nth-of-type(2) > tbody > tr > td > table > tbody > tr:nth-of-type(2) > td > table > tbody > tr:nth-of-type(9) > td:nth-of-type(3) > table,
  .adsbygoogle,
  .article_b_right
  {
    display: none !important;
  }

  table:nth-of-type(2) > tbody > tr > td > table > tbody > tr:nth-of-type(2) > td > table > tbody > tr:nth-of-type(9) > td:nth-of-type(1) {width: 100%;}
  table:nth-of-type(2) > tbody > tr > td > table > tbody > tr:nth-of-type(2) > td > table > tbody > tr:nth-of-type(9) > td:nth-of-type(1) > table {width: 700px;}

  table {
    float: none !important;
    margin: 0 auto !important;
  }

  table, tbody, tr, td {
    border: none !important;
    background: none !important;
  }

  #articleBody {
    font-family: "KoPubBatang", "나눔명조", "맑은 고딕", "나눔바른고딕", "나눔고딕" !important;
    line-height: 1.85em !important;
  }

  #articleBody {
    font-size: 1.1em !important;
    word-break: keep-all;
    text-align: justify;
  }
}
