css:
@-moz-document domain("readersnews.com") {
  #ND_Warp > table:nth-of-type(2) > tbody > tr > td:nth-of-type(3) > table:nth-of-type(2) > tbody > tr > td,
  .adsbygoogle {
    display: none !important;
  }

  #ND_Warp > table:nth-of-type(2) > tbody > tr > td:nth-of-type(1) {width: 700px !important;}
  #ND_Warp > table:nth-of-type(2) > tbody > tr > td:nth-of-type(2),
  #ND_Warp > table:nth-of-type(2) > tbody > tr > td:nth-of-type(3) {display: none !important;}

  #ContentsSub {
    background-image: none !important;
  }

  #ND_Warp > table:nth-of-type(2) > tbody > tr > td:nth-of-type(1) > table:nth-of-type(3) {
    float: none !important;
    margin: 0 auto !important;
    border: none !important;
    background-image: none !important;
  }

  #articleBody,
  #articleBody > p{
    font-family: "KoPubBatang", "나눔명조", "맑은 고딕", "나눔바른고딕", "나눔고딕" !important;
    line-height: 1.85em !important;
  }

  #articleBody,
  #articleBody > p {
    font-size: 1.2em !important;
    word-break: keep-all;
    text-align: justify;
  }
}
