css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.esaimaa.fi") {
    .container {
    width: 1032px !important;
}
body,
div.nl-item .nl-info {
    font-family: "helvetica neue";
}
#menu1,
#kavi-login a {
    font-family: "helvetica neue";
    font-size: 12px;
    border-bottom: 0px solid #f00 !important;
    margin: 0 8px 0px 12px;
}
div.image_author {
    width: 100%;
    text-align: left;
    font-size: 12px;
    font-family: "helvetica neue";
}
#paivyritiedot,
#menu3 {
    font-size: 16px;
    font-family: "helvetica neue";
}
.news-imagelist,
li.news-list a,
.teksti ul a {
    font-size: 12px;
    font-family: "helvetica neue";
}
ul.news-list li.news-list {
    margin: 0px 10px 0px 15px;
}
#menu2 {
    display: block;
    font-family: "helvetica neue";
    font-size: 16px;
}
#menu2 ul li {
    padding: 8px 2px;
    border: none;
}
#menu2 {
    background-color: #EDF0F2;
}
#topmenuright {
    font-family: "helvetica neue";
    font-size: 12px;
    margin: 0px 8px;
}
h1 {
    font-size: 26px;
    font-family: "PT Sans";
}
h2 {
    font-size: 22px;
    font-family: "PT Sans";
}
#c1,
#c1b {
    width: 662px;
    margin: 12px 25px 9px 35px;
}
.container {
    width: 1180px;
}
.dividerborder,
#KiinteistoWide,
#c2_elevator {
    padding: 0px 0px 20px !important;
    border-bottom: 0px #fff;
    margin-bottom: 20px !important;
}
#menu1 ul li.selected,
#menu3 ul li.selected {
    background: #fff;
}
#menu2 ul li.selected {
    background-color: #edf0f2;
}
#menu1 ul li.selected a,
#menu2 ul li.selected a,
#menu3 ul li.selected a {
    color: #000;
}
#kavi-login {
    margin: 0 16px 0 8px;
}
p.selfpromo {
    margin: 5px 0px 10px 50px;
}
}
