css:
@-moz-document domain("leagueoflegendsfunnyvideos.com") {
/* nav */
.wsb-navigation-rendered-top-level-menu.nav-subnav a {
    border-bottom: 1px solid #fff !important;
	color: #666 !important;
}
.wsb-navigation-rendered-top-level-menu.nav-subnav a:hover {
	border-bottom-color: #c6b170 !important;
    color: #000 !important;
}


/* video */
#wsb-canvas-template-container {
	position: relative !important;
}
#wsb-canvas-template-container > .wsb-element-youtube{
	position: relative !important;
    top: 0 !important;
    left: 0 !important;
}
.page {
	max-width: 100%;
}
.youtube {
	position: relative !important;
	padding-bottom: 56.25% !important;
	padding-top: 25px !important;
	height: 0 !important;
    width: 100% !important;
}
.youtube iframe {
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
    height: 100%;
}

/* adds */
.adsbygoogle {
	display: none !important;
}
}
