css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.nihongo.aikidoka.ru/memory.html") {
.left_block,
.right_block,
.contop,
.scrollTop,
.quote,
h3,
#dle-content p,
ol,
.speedbar {
    display: none !important;
}
.footer {
    position: absolute;
    right: -10000px;
}
.content {
    background: none !important;
}
.footer,
.foot {
    display: none !important;
    width: 0px;
    height: 0px
}
.footer * {
    display: none !important;
    width: 0px;
    height: 0px
}
a[href="javascript:ShowOrHide('memory_instr')"] {
    display: none !important;
}
.memtabs.correct .card {
    display: none !important;
}
.container {
    min-height: 96% !important;
    height: 96% !important;
}
}
