css:
@-moz-document domain(ign.com) {
#header-container, #header-container #header-nav.no-spine.watchread {
    background:none !important;
    transition:background 0.5s !important;
    box-shadow:none !important;
    -webkit-box-shadow:none !important;
}
#header-container #header-nav.no-spine.watchread:hover {
    background:#fff !important;
    transition:background 0.5s !important;
}
#header-container #header-nav#header-nav.no-spine.watchread .dpad-container.dpad-displayed, #header-nav.no-spine.watchread #ign-shows, #header-nav.no-spine.watchread .popular-links {
    opacity:0 !important;
    transition:opacity 0.5s !important;
}
#header-container #header-nav#header-nav.no-spine.watchread:hover .dpad-container.dpad-displayed, #header-container #header-nav#header-nav.no-spine.watchread:hover #ign-shows, #header-container #header-nav#header-nav.no-spine.watchread:hover .popular-links {
    opacity:1 !important;
    transition:opacity 0.5s !important;
}
.spine.global.fixed {
    opacity:0 !important;
    transition:opacity 0.5s !important;
}
.spine.global.fixed:hover {
    opacity:1 !important;
    transition:opacity 0.5s !important;
}
}
