css:
@-moz-document url-prefix("http://forum.wacom.eu/") {
body 
{background-attachment:fixed!important;
background: -moz-linear-gradient(top, #e5fcff 0%, #fff4f8 100%);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#e5fcff), color-stop(100%,#fff4f8));
background: -webkit-linear-gradient(top, #e5fcff 0%,#fff4f8 100%);
background: -o-linear-gradient(top, #e5fcff 0%,#fff4f8 100%);
background: -ms-linear-gradient(top, #e5fcff 0%,#fff4f8 100%);
background: linear-gradient(to bottom, #e5fcff 0%,#fff4f8 100%); 
}

.navbar, .forabg, .forumbg, .panel, .bg1, .bg2, .bg3, ul.forums, #tabs .activetab a, #minitabs li {background:#fff !important;}

.headerbar, table.table1 tbody tr:hover, table.table1 tbody tr.hover, li.row:hover {background: #95dbf7 !important;}

#tabs .activetab a span, #search-box , .navbg a, .forumbg .header a, .forabg .header a, th a {color: #95dbf7 !important;}

li.row, ul.navlinks, table.table1 tbody td {border: 1px #95dbf7 solid !important;}
.content, label, * {color: #333;}
a:link, a:visited { color: #898989 !important; }
a:hover { color: #000 !important;}


}
