css:
/* AGENT_SHEET */

@charset "utf-8";

scrollbar thumb {
    -moz-appearance: none !important;
    border: 0px solid rgba(255,255,255,0.6) !important;
    padding: 0 0 !important;
    background: rgb(245,141,0) !important;
    box-shadow: 0px 0px 1.5px white !important;
    }

scrollbar[orient="vertical"] thumb {
    min-height: 70px !important;
    width: 15px !important;
}

scrollbar[orient="horizontal"] thumb {
    min-width: 70px !important;
    height: 11px !important;
}

scrollbar[orient="vertical"] thumb:hover, scrollbar[orient="vertical"] thumb:active {
    background: rgb(250,45,36) !important;
    }

scrollbar[orient="horizontal"] thumb:hover {
    background: rgb(250,45,36) !important;
    }

scrollbar
{    
    -moz-appearance: none !important;
    background: #333 !important;
    position: relative !important;
    border: none !important;
    z-index: 102400 !important;
}
