@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.twoo.com") {
  
  .activityPage .userGrid__avatar.roundedAvatar.maxAvatar {
    height : 200px !important;
    width: 200px !important;
  } 
  .speechBubble__messageWrapper__image .roundedAvatar.maxAvatar {
    height : 180px !important;
    width: 180px !important;
  }
  .maxAvatar {
    width: 400% !important;
    max-width: none !important;
  }
  
  .speechBubbleContainer .speechBubble__messageWrapper__item {
	margin-left: 150px;
}
  .speechBubbleContainer .speechBubble__messageWrapper__image {
   position: unset;
}
  .inboxContainer .tw3-personList li .tw3-person__info__title, .inboxContainer .tw3-personList li .tw3-person__info__description {
	color: #FFFFFF;
	font-size: medium;
   font-weight: bold;
   background:gray;
   opacity: 0.8;
}
  .tw3-person__info{
    margin-top: 120px;
  }
    
  .photos__container li {
	float: left;
	height: 180px;
	width: 180px;
	margin-right: 2px;
}
  
  .activity .peopleList li .peopleList__item__info {
	  padding-left: 165px;

}
    
}
