css:
@-moz-document domain(forum.ubuntu-tr.net) {
a {
    color: #AEA79F!important;
}
body {
    background: url("https://forum.ubuntu-tr.net/Themes/ubuntu/images/pattern6.png") repeat scroll 0 0 #000!important;
}
#navbar,
#navbar .active,
.catbg {
    background: #333!important;
}
#nav-global {
    background: url("https://forum.ubuntu-tr.net/Themes/ubuntu/images/pattern6.png") repeat scroll 0 0 #000!important;
}
#menu-ustmenu>li>a {
    color: white!important;
}
.greeting {
    color: #555!important;
}
.greeting+div {
    color: #777!important;
}
.usermenu>li>a {
    background: #222!important;
}
.haber {
    background: #080808!important;
}
.signature {
    color: #999!important;
}
.windowbg,
.windowbg2,
.lockedbg,
.lockedbg2,
.stickybg,
.stickybg2 {
    background: url("https://forum.ubuntu-tr.net/Themes/ubuntu/images/pattern6.png") repeat scroll 0 0 #111!important;
}
.windowbg .inner,
.windowbg2 .inner,
[itemprop="datePublished"],
.modified,
.smalltext,
[itemprop="author"]>ul>li {
    color: white!important;
}
.topic_table td {
    color: #666!important;
}
.postwindow {
    background: url("https://forum.ubuntu-tr.net/Themes/ubuntu/images/pattern6.png") repeat scroll 0 0 #111!important;
}
.board {
    background: transparent!important;
}
.bbc_code {
    background: #111!important;
}
.bbc_standard_quote {
    background: url("https://forum.ubuntu-tr.net/Themes/ubuntu/images/pattern6.png") repeat scroll 0 0 #444!important;
}
.buttonlist a {
    color: #ddd!important;
}
#detailedinfo,
#basicinfo h4 {
    color: #666!important;
}
.keyinfo h5 {
    color: #888!important;
}
.roundframe,
.innerframe {
    background: transparent!important;
}
#colophon {
    background: #222!important;
}
.site-info {
    color: #eee!important;
}
#menu-global-links>li>ul>li>a {
    color: #999!important;
}
#menu-global-links>li>a {
    color: #eee!important;
}
}
