css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://), url-prefix(https://) {
a, span , h1 , h2,h3,h4,h5,h6 , strong , li , pre , p , em , label,input{
  position: relative;
}
a:after  , span:after , h1:after , h2:after , h3:after , h4:after , h5:after , h6:after , strong:after , li:after , pre:after , p:after , em:after , label:after , input:after{
  content: "hodor hodor hodor";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: white;
}
    
div{
  position: relative;
}
div:after{
  content: "hodor hodor";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
}
    }
