css:
@-moz-document domain("loggly.com") {
div.loggly-content .event-row {
  font-family: "Ubuntu Mono" !important;
  font-size: 10px !important;
}
div.loggly-content .grid-table {
  font-size: 12px !important;
}
div.loggly-content .flex-content .column-timestamp {
  width: 145px !important;
}

div.loggly-content .subtab-settings-toggle {
  display: none !important;
}
div.loggly-content .subtab-settings-panel {
  width: 260px !important;
  height: 29px !important;
  display: block !important;
  margin-top: -20px !important;
  margin-right: -0 !important;
}
div.loggly-content .subtab-settings-panel li {
  float: left !important;
  line-height: 29px !important;
  height: 29px !important;
}
div.loggly-content .subtab-settings-panel li:first-child {
  border-right: none !important;
}
div.loggly-content .subtab-settings-panel li label {
  cursor: pointer;
}
}
