css:
@-moz-document url-prefix("https://ask.fm") {
#topMenu > .topBar a {
	color: #000;
}
#topMenu > .topBar .btn-action {
	color: #000;
}
.subTabBar-wide a.active {
	border-bottom: 3px solid #000;
}
.askfm-ad-slot {
    display: none;
    visibility: hidden;
}
.aboutMore > p:first-child:after {
    content: "...";
}
#profileBackgroundOverlay > #profileHeader-wrapper > a:first-child {
	background-image: url(http://static.tumblr.com/d51ca9efec62167176657cdeedb1dcda/vgeb2j6/ySPnblbbd/tumblr_static_9z557y1l0zk0ccssogos04scw.jpg) !important;
}
footer > nav {
    visibility: hidden;
}
footer > nav:after {
    content: "What you really use these links?";
    visibility: visible;
}
}
