/** 2016.01.20 eldono 1a, de lusentoj: www.lusentoj.neocities.org **/

  th, b, h2 {font-weight: normal; font-style: normal;}
  body, tr, tbody, b, pre {color: #f2f2f2 !important;}
  
  .tcolor2, body, td, tr, .tcolor3 {background-color: #111;}
  
  .tcolor1, th, textarea, input {background-color: #446e99;}
  textarea, input {border: 0px; font-size: 30px;}
  
  text-area {font-size: 30px;}
  
  input.small {height: 25px; width: 25px;}
  
  td, h2 {color: #83C53D;}
  
  .warning {background-color: #6a2f2f;}
  
  a, a:visited, a:link {color: #446e99 !important; text-decoration: none;}
  a:hover, a:link:hover {color: #83C53D !important; text-decoration: none;}
