css:
@-moz-document url-prefix("https://booking.skiddoo.com.au/") {
header {
    background-color: #303030;
}

header .logo img {
	display: none;
}

header {
	background-color: #f04e58;
    border-bottom: 1px solid rgba(255, 197, 201, .5);
}

header .logo {
	background-image: url(https://booking.skiddoo.com.au/static/lib/skiddoo-foundation/skiddoo/svg/logo/skiddoo-logo-white.svg);
    background-size: cover;
}

header .contact,
header .ski-phone-number,
header .ski-phone-number-icon {
	color: white;
}

.quick-filters-toggle-button,
.quick-filters-toggle-button:hover,
.quick-filters-toggle-button:active,
.quick-filters-toggle-button:focus {
    background-color: #0d99cc!important;
    color: white;
}

.ski-checkbox>[type=checkbox]:checked+.ski-checkbox-icon:before {
	color: #f04e58;
}

.expand-button.ski-expandable-toggler {
    background-color: #f0f0f0!important;
    color: #222;
    font-weight: normal;
}

.book-button,
.book-button:hover,
.book-button:active,
.book-button:focus {
    background-color: #f1515b!important;
}

}
