css:
@-moz-document url("http://localhost:2368/"), url("https://feedly.com/i/my"), url-prefix("http://127.0.0.1:2368") {
@font-face {
    font-family: Shabnam;
    src: url(https://rastikerdar.github.io/shabnam-font/Shabnam.woff?v=0.7.2);
    src: url(https://rastikerdar.github.io/shabnam-font/Shabnam.woff?v=0.7.2#iefix) format('embedded-opentype'), url(https://rastikerdar.github.io/shabnam-font/Shabnam.woff?v=0.7.2) format('woff'), url(https://rastikerdar.github.io/shabnam-font/Shabnam.woff?v=0.7.2) format('truetype');
    font-weight: normal;
}
html {
    font: 62.5%/1.65 "Shabnam","Open Sans",sans-serif;
}
.content-preview-content *, .entry-preview-content *, .editor .markdown-editor, .view-header {
    direction:rtl;
}
blockquote {
    border-right: #dfe1e3 .6em solid;
    border-left: none;
}
.content-list .entry-title {
    direction:rtl;
    text-align:right;
}
.content-preview .post-controls {
    left: 25px;
}
.editor-options .dropdown-menu {
    left: 0;
    right: auto;
}
.content-preview .content-preview-content,.editor .entry-preview-content {
    word-break: normal;
}
}
