css:
@-moz-document url-prefix("https://www.joicaster.co/chat/") {
body {
    background-color: rgba(0, 0, 0, 0);
    margin: 0px auto;
    overflow: hidden;
}

div.chat-tabs.ng-scope {
    display: none;
}

div#chat-log {
    border: none;
    position: absolute;
    top: 0px;
    bottom: 0px;
    margin: auto;
}

div#chat-send,#poweredby, input.form-control.ng-pristine.ng-valid, a.btn.btn-audio.btn-success, a.btn.btn-primary {
    display: none;
}

#chat-log > ul > li {
    border: none;
    padding-left: 0;
    padding-right: 0;
}

#chat-log > ul > li > div > i, #chat-log > ul > li > div > img {
    left: 2px;
}

#chat-log > ul > li > div span.username:after {
    content: ":";
}

#chat-log > ul > li > div span.comment {
}

#chat-log > ul > li > .timestamp {
    right: 5px;
}

#chat-log > ul > li.Bot:first-child {
    display: none;
}
}
