css:
@-moz-document domain("www.unovarpg.com") {
body>div:nth-of-type(3)>div:nth-of-type(3), 
body>div:nth-of-type(3)>div:nth-of-type(2)>div:nth-of-type(1), 
body>div:nth-of-type(4)>div:nth-of-type(3), 
body>div:nth-of-type(4)>div:nth-of-type(2)>div:nth-of-type(1), 
div.leaderboard, 
div.addthis_sharing_toolbox div.basicContainer, 
div>div:nth-of-type(2)>p:nth-of-type(1), 
div.addthis_32x32_style, 
div.basicContainer>div>div:nth-of-type(2)>div:nth-of-type(2)>div:nth-of-type(2), 
.headerSocial, img[width="1"][height="30"] {
    display:none
}
#headerBar, .header {
    z-index: 6;
    height: 45px !important;
}
#buttonMenu{
    position:fixed !important;
    z-index: 6;
    top: 8px !important;
}
div.basicContainer {
    position:relative;
    bottom: 70px;
    margin-top: 0px;
    padding-top: 0px;
}
#logoimg, img[width="192"][height="45"] {
    height: 30px;
}
img[width="192"][height="45"] {
    position:relative;
    left: 570px;
    visibility:visible !important;
}
#PokemonZone, #PokemonZone img {
    width: auto; height: 100px; 
    left:80px;
}
#PokemonZone {
    position:absolute;
    top:0px;
}
}

@-moz-document url-prefix("https://www.unovarpg.com/room.php?room=") {

body>div:nth-of-type(3)>div:nth-of-type(2)>div:nth-of-type(3)>div.basicContainer>div>div:nth-of-type(2)>div:nth-of-type(2)>div:nth-of-type(3)>div.memberPanelTable {
    width: 300px !important;
    padding: 0px !important;
}
#subscriptionDiv {
    position:absolute;
    right: 0px; top: 4px;
}
div.memberPanelTable a {
    position:relative;
    right: 70px;
}

}

@-moz-document url-prefix("https://www.unovarpg.com/map.php?map=") {
div.basicContainer>div>div:nth-of-type(2)>div:nth-of-type(2)>h1 {
    display:none;
}
}
