css:
@-moz-document domain("discordapp.com") {
.channel-voice-states li {
    display: inline-block;
    margin-left: 3px;
}
.channel-voice-states li div span::after {  content: ", "; }
.channel-voice-states li div { display: inline-block; }
.channel-voice-states li div span { display: inline-block; }
.channel-voice-states li div .avatar-small { margin:0 !important; width:13px !important; height:13px !important; background-size: 13px 13px !important; }
.channel-voice-states li div i { display: none !important; }
.channels-wrap { width: 300px; }
}
