css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("doope.jp") {
/** AD **/
#head_box,ins,.cont_related {
display:none !important;
}


/** Layout **/
#cont {
margin-left: 200px !important;
}

#menu,#phead,#side,#pickupbox,.cont_articlebox_02a,#fwrap,#cont_special,#cont_special img {
display:none !important;}

/**color*/
#pbody {
border: none !important;
}

#wrap,#cwrap,.cont_box,#pbody,.cont_titbox,.cont_footbox_03 {
background: url("http://")  !important;
}

.cont_titbox {
background: #444 !important;
border: #f0f0f0 solid 1px !important;
}

.cont_footbox_03 {
background: #333 !important;
color: #f0f0f0 !important;
}

body,#wrap {
background: #333 !important;
}


p,.cont_article ul,.pages {
color: #f0f0f0 !important;
}

.current,.extend, a {
color: #FFC50E !important;
}

span.current {
border-color: #E8930A !important;
}

blockquote {
color: #f0f0f0 !important;
background: #333 !important;
}

.cont_via {
background: #222 !important;
color: #f0f0f0 !important;
padding-right: 10px !important;
}

.cont_footbox_03 a {
color: #39F !important;
}

/**SNS**/
.tboxfl01,.twt,.fb_f {
display:none !important;
}

}
